import { useState } from 'react';
import CourseList from './CourseList';
import useFetch from './useFetch';

const Home = () => {
  const {
    data: courses,
    isPending,
    error,
  } = useFetch('http://localhost:8000/courses');

  const [query, setQuery] = useState('');

  return (
    <div className='home'>
      <h2 className='text-3xl font-extrabold text-center leading-none'>
        Hello World
      </h2>
      <div className='m-5'>
        <input
          type='text'
          placeholder='Search'
          className='bg-gray-50 border border-gray-300 text-gray-900 text-lg rounded-xl pl-5 p-1.5 block w-full max-w-lg m-auto'
          onChange={(e) => setQuery(e.target.value)}
        />
      </div>
      {error && <div>{error}</div>}
      {isPending && <div>Loading...</div>}
      {courses && query && <CourseList courses={courses} query={query} />}
    </div>
  );
};

export default Home;
