import { useParams } from 'react-router-dom';
import useFetch from './useFetch';

const CourseDetails = () => {
  const { id } = useParams();
  const {
    data: course,
    error,
    isPending,
  } = useFetch('http://localhost:8000/courses/' + id);

  return (
    <div className='course-details'>
      {isPending && <div>Loading...</div>}
      {error && <div>{error}</div>}
      {course && (
        <article>
          <h2 className='underline underline-offset-4 decoration-4  decoration-neutral-600 text-xl font-bold mb-4'>
            {course.Монгол_нэр}
          </h2>
          <p className='text-justify'>{course.Зорилго}</p>
        </article>
      )}
    </div>
  );
};

export default CourseDetails;
