import { useNavigate, useParams } from 'react-router-dom';
import useFetch from './useFetch';

const CourseDetails = () => {
  const { id } = useParams();
  const {
    data: course,
    error,
    isPending,
  } = useFetch('http://localhost:8000/courses/' + id);
  const navigate = useNavigate();
  const handleClick = () => {
    fetch('http://localhost:8000/course/' + course.id, {
      method: 'DELETE',
    }).then(() => {
      navigate('/');
    });
  };

  return (
    <div className='blog-details'>
      {isPending && <div>Loading...</div>}
      {error && <div>{error}</div>}
      {course && (
        <article>
          <h2>{course.title}</h2>
          <p>Written by {course.Зорилго}</p>
          <div>{course.body}</div>
          <button onClick={handleClick}>delete</button>
        </article>
      )}
    </div>
  );
};

export default CourseDetails;
